import java.util.Scanner;public class Main {    static Scanner sc = new Scanner(System.in);    public static void main(String[] args) {        while (sc.hasNext()) {            int n = sc.nextInt();            int[] arr = new int[n];            for (int i = 0; i < n; i++) {                arr[i] = sc.nextInt();            }            Long max = 0L;            for (int i = 0; i < n; i++) {                Long mult = 1L;                for(int j = i; j < n; j++){                    mult *= arr[j];                    max = max > mult ? max : mult;                }            }            if(max < 0){                System.out.println(-1);            }else {                System.out.println(max);            }        }    }}