import java.util.*;class Main{        public static void main(String[] args){        Scanner scanner = new Scanner(System.in);        while(scanner.hasNext()){            int a = scanner.nextInt();            while(a-- != 0){                int count = 0;                String s = scanner.next();                for(int i=0; i<(s.length()>>1); ++i){                    if(s.charAt(i) != s.charAt(s.length()-i-1)){                        count++;                    }                    if(count > 1){                        System.out.println("NO");                        break;                    }                }                if(count == 1 || (count == 0 && s.length()%2 != 0)){                    System.out.println("YES");                }                else if(count == 0 ){                    System.out.println("NO");                }            }        }    }}