import java.util.Scanner;import java.math.BigInteger;class Main{    public static void main(String[] args){        Scanner scanner = new Scanner(System.in);        int a = scanner.nextInt();        BigInteger sum = null;                while(a-- != 0){            sum = new BigInteger("0");            for(int i=0; i<4; ++i){                sum.add(new BigInteger(scanner.next()));            }            System.out.println(sum);        }            }}