import java.util.*;public class Main {    public static void main(String[] args) {        for(int x1 = 0; x1 <= 100; x1++)            for(int x2 = 0; x2 <= 100; x2++)                for(int x3 = 0; x3 <= 100; x3+= 3){                    if(x1 + x2 + x3 != 100)                        continue;                    if(x1 * 5 + x2 + x3 / 3 == 100)                        System.out.println(x1 + " " + x2 + " " + x3);                }    }}