import java.util.*;import java.math.*;import java.math.*;public class Main {    public static void main(String[] args)    {        Scanner scan=new Scanner(System.in);         int n=scan.nextInt();         int[] a=new int[n];         for(int i=0;i<n;i++)         {        	 a[i]=scan.nextInt();         }         int max=0;         int p=0;         for(int i=n-1;i>=0;i--)         {        	 if(a[i]>=max)        	 {        		 max=a[i];        		 p=i;        	 }         }                  System.out.println(p+1);                               }    			  }