import java.util.Scanner;public class Main {	static Scanner sc = new Scanner(System.in);	public static void main(String[] args) {		int n = sc.nextInt();		int res = 0;		int max = 0;		while (n-- > 0) {			int a = sc.nextInt();			int b = sc.nextInt();			if (a >= 90 && a <= 140 && b >= 60 && b <= 90) {				res++;				max = Math.max(res, max);			} else {				res = 0;			}		}		System.out.println(max);	}}