import java.util.HashMap;import java.util.Scanner;public class Main {	static Scanner sc = new Scanner(System.in);	public static void main(String[] args) {		int n = sc.nextInt();		HashMap<Integer,Integer> map = new HashMap<>(100);		while(n-- > 0) {			int num = sc.nextInt();			map.put(num, map.getOrDefault(num, 0)+1);		}		int num = sc.nextInt();		System.out.println(map.getOrDefault(num,0));	}}