import java.util.Scanner;public class Main {    public static char[] rotate(char[] seq, int c) {        char[] tmp = new char[seq.length];        c = c % seq.length;        for (int i = 0; i < seq.length; i++) {            tmp[i] = seq[(i + c) % seq.length];        }        return tmp;    }    public static void calc() {        Scanner scanner = new Scanner(System.in);        char[] char_sequence = scanner.next().toCharArray();        int n = scanner.nextInt();        while (n-- > 0) {            int a = scanner.nextInt();            int b = scanner.nextInt() + 1;            int c = scanner.nextInt();            char[] tmp = new char[b - a];            System.arraycopy(char_sequence, a, tmp, 0, b - a);            System.arraycopy(rotate(tmp, c), 0, char_sequence, a, b - a);        }        System.out.println(char_sequence);    }    public static void main(String[] args) {        calc();    }}