import java.util.Scanner;public class Main {	final static Scanner sc = new Scanner(System.in);	final static char[] data = { 'A', 'B', 'C', 'D', 'E', 'F' };	public static void main(String[] args) {		int n = sc.nextInt();		int radix = sc.nextInt();		StringBuilder sb = new StringBuilder();		while (n != 0) {			int r = n % radix;			if (r > 9) {				sb.append(data[r % 10]);			} else {				sb.append(r);			}			n /= radix;		}		System.out.println(sb.reverse());	}}