import java.util.*;public class Main {	public static void main(String[] args) {		int tmp = fib(20190324);		String str = String.valueOf(tmp);		str = str.substring(str.length() - 4);		System.out.println(Integer.parseInt(str));	}	public static int fib(int n) {		int a = 1,b = 1,c = 1,sum = 0;		for (int i = 4; i <= n; i++) {			sum = (a + b + c) % 10000;			a = b % 10000;			b = c % 10000;			c = sum;		}		return sum;	}}