import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        int num=sc.nextInt();       if(num%2==0)           System.out.println("0.5000000000");       else{           double temp=(num/2)+1;          System.out.println(String.format("%.10f", temp));       }    }}