import java.util.*;class Main{    public static void main (String[] args) {        Scanner scanner = new Scanner(System.in);        while(scanner.hasNext()){            int n = scanner.nextInt();            int[] a = new int[n+1];            for(int i=1;i<=n;++i){                a[scanner.nextInt()] =i;            }            for(int i=1;i<=n;++i){                System.out.print(a[i]);                        }            System.out.println();        }    }}