import java.util.ArrayList;import java.util.Collections;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        int day = input.nextInt();        ArrayList list = new ArrayList<>();        for (int i = 0; i < day; i++) {            ArrayList list2 = new ArrayList<>();            int res_num = input.nextInt();            int s = input.nextInt();            int[][] arr = new int[res_num][2];            for (int j = 0; j < res_num; j++) {                for (int k = 0; k < 2; k++) {                    arr[j][k] = input.nextInt();                }            }            int fx =  0;            for (int j = 0; j < res_num; j++) {                if(arr[j][1]<=s){                    fx = arr[j][0];                }else {                    fx = arr[j][0] - (arr[j][1] - s);                }                list2.add(fx);            }            Collections.sort(list2);            list.add(list2.get(list2.size()-1));        }        for (int i = 0; i < list.size(); i++) {            System.out.println("Case #"+ (i+1)+":" +list.get(i));        }    }}