// package Day1_12;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        boolean flag = false;        for(int i =1;i<10;i++){            if (i*1 == n || i*2 == n || i*3 == n || i*4 == n || i*5 == n || i*6 == n || i*7 == n || i*8 == n || i*9 == n) {                flag = true;                break;            }        }        if (flag||n==1){            System.out.println("Yes");        }        else {            System.out.println("No");        }    }}