import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        int n=input.nextInt();        int m=input.nextInt();        int[] a=new int[m];        int t=0;        for (int i = 0; i < m; i++) {            a[i]=input.nextInt();            t+=a[i];        }        if (t<n)            System.out.println(n-t);        else System.out.println(-1);    }}