import java.util.*;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        while (scanner.hasNext()){            String s = scanner.nextLine();            Set set = new HashSet();            for (int i = 0; i < s.length(); i++) {                set.add(s.charAt(i));            }            Object[] strings = new Object[set.size()];            Iterator iterator = set.iterator();            for (int i = 0; i < set.size(); i++) {                System.out.print(iterator.next());            }            System.out.println();        }    }}