import java.util.*;import java.math.*;import java.math.*;public class Main {	public static void main(String[] args)	{		Scanner scan=new Scanner(System.in);					String s1,s2;			int[] a1 = new int[100];			int[] a2 = new int[100];			Arrays.fill(a2, 0);			Arrays.fill(a1, 0);						s1=scan.next();			s2=scan.next();			for(int i=0;i<s1.length();i++)			{				a1[i]=s1.charAt(s1.length()-1-i)-'0';			}			for(int i=0;i<s2.length();i++)			{				a2[i]=s2.charAt(s2.length()-1-i)-'0';			}			int xib=0;			for(int i=0;i<100;i++) {				a1[i]=a1[i]+a2[i]+xib;				if(a1[i]>=10) {					a1[i]-=10;					xib=1;				}				else {					xib=0;				}			}			int jj;			for(jj = 99;jj>=0;jj--) {				if(a1[jj]!=0) break;			}			for(int i =jj;i>=0;i--) {				System.out.print(a1[i]);			}								}}