import java.math.BigInteger;import java.util.Locale;import java.util.Scanner;import java.util.Stack;public class Main {    static int n;    public static void main(String[] args) {        Scanner cin = new Scanner(System.in);        int[] dict = new int[26];        boolean flag = false;        while(cin.hasNext())        {            String line = cin.nextLine();            for(int i=0;i<line.length();i++){                if(line.charAt(i) == '#'){                    flag = true;                    break;                }                if(line.charAt(i) >= 'a' && line.charAt(i) <= 'z'){                    int j = line.charAt(i) - 'a';                    dict[j]++;                }            }            if(flag){                for(int i=0;i<26;i++){                    System.out.println((char) ('a'+i)+" "+dict[i]);                }            }        }    }}