import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner cin = new Scanner(System.in);        while(cin.hasNext())        {            int n = cin.nextInt();            cin.nextLine();            while(n > 0)            {                String str = cin.nextLine();                int diff = 0;                for(int i=0;i<str.length()/2;i++)                {                    if(str.charAt(i) != str.charAt(str.length()-1-i))                        diff++;                }                if(diff == 1)                    System.out.println("YES");                else                    System.out.println("NO");                n--;            }        }    }}