import java.util.Scanner;public class Main {    static Scanner sc = new Scanner(System.in);    public static void main(String[] args) {        int n = sc.nextInt();        sc.nextLine();        while (n-- > 0) {            String s = sc.nextLine();            int cnt = 0;            int len = s.length();            for (int i = 0; i*2 < len; i++) {                if(s.charAt(i) != s.charAt(len-1-i)){                    cnt++;                    if(cnt > 1){                        break;                    }                }            }            if(cnt == 1 || ( cnt == 0 && (len & 1) == 1) ){                System.out.println("YES");            }else {                System.out.println("NO");            }        }    }}