import java.math.BigDecimal;public class Main {    public static void main(String[] args) {        BigDecimal bigDecimal = new BigDecimal(1);        BigDecimal bigDecimal1;        BigDecimal bigDecimal2;        BigDecimal bigDecimal3;        for(int i=1;i<100000;i++){            bigDecimal1 = new BigDecimal((2*i));            bigDecimal2 = new BigDecimal((2*i-1));            bigDecimal3 = new BigDecimal((2*i+1));            bigDecimal = bigDecimal.multiply(bigDecimal1).divide(bigDecimal2,300,BigDecimal.ROUND_HALF_UP).multiply(bigDecimal1).divide(bigDecimal3,300,BigDecimal.ROUND_HALF_UP);        }        String s = bigDecimal.toString();        System.out.println(s.charAt(101));    }}