import java.util.Arrays;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int i, a[] = new int[n];        for (i = 0; i < n; i++) {            int x = sc.nextInt();            a[i] = x;        }        Arrays.sort(a);        int count1 = 0, count2 = 0, count3 = 0, count4 = 0;        for(i=0;i<n;i++){            if(a[i]>=0&&a[i]<=18)count1++;            else if(a[i]>=19&&a[i]<=35)count2++;            else if(a[i]>=36&&a[i]<60)count3++;            else count4++;        }        System.out.printf("%.2f%%\n",1.0*count1/n*100);        System.out.printf("%.2f%%\n",1.0*count2/n*100);        System.out.printf("%.2f%%\n",1.0*count3/n*100);        System.out.printf("%.2f%%\n",1.0*count4/n*100);    }}