import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int i, a[] = new int[7], b[] = new int[7];        for (i = 0; i < 7; i++) {            int x = sc.nextInt();            int y = sc.nextInt();            a[i] = x;            b[i] = y;        }        int max=0,k=0;        for (i = 0; i < 7; i++) {            if(a[i]+b[i]>max&&a[i]+b[i]>8){                max=(a[i]+b[i]);                k=i+1;            }        }        System.out.println(k);    }}