//package day2;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        int k=sc.nextInt();        StringBuilder sb=new StringBuilder(sc.next());        boolean f=false;        for (int i = 0; i < sb.length()-k; i++) {            boolean flag=false;            for (int j = i+1; j <i+k ; j++) {                if(sb.charAt(i)!=sb.charAt(j)) {                    flag = true;                    break;                }            }            if(!flag){                System.out.println(sb.charAt(i));                f=true;                break;            }        }        if(!f){            System.out.println("No");        }    }}