import java.util.Scanner;public class Main {    public static void calc(String inputs) {        String[] arr = inputs.split(",");        int index1 = arr[0].indexOf(arr[1]);        int index2 = arr[0].lastIndexOf(arr[2]);        if (index1 == -1 || index2 == -1 || index1 + arr[1].length() > index2) {            System.out.println("-1");        } else {            System.out.printf("%d\n", index2 - arr[1].length());        }    }    public static void main(String[] args) {        Scanner s = new Scanner(System.in);        calc(s.nextLine());    }}