import java.util.Scanner;public class Main {    public static void main(String[] args){        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        int cnt = 0;        for(int i = 11;i<=n;i++){            boolean flag1 = true;            for(int j = 2;j<=i-1;j++){                if(i%j==0){                    flag1 = false;                    break;                }            }            if(flag1){                String str = String.valueOf(i);                StringBuilder sb = new StringBuilder(str);                if(str.equals(sb.reverse().toString())){                    cnt++;                }            }        }        System.out.println(cnt);    }}