import java.util.Scanner;public class Main {	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int a = sc.nextInt();		int b = sc.nextInt();		int c = sc.nextInt();		int n = sc.nextInt();		System.err.println(f(a,b,c,n));	}	static int f(int a, int b, int c, int n) {		if(a<0 || b<0 || c<0) return 0;		if(n==0) return 1; 				return f(a-1,b,c,n-1)+f(a,b-1,c,n-1)+f(a,b,c-1,n-1);	}}