import java.util.Scanner;public class Main {	public static void main(String[] arge)	{		Scanner in=new Scanner(System.in);		long num=0;		for(int i=1;i<2020;i++)		{			int n=i;			int s=0;			while(n>0)			{				int k=n%10;				n/=10;				if(k==2||k==1||k==0||k==9)				{					s=1;					break;				}			}			if(s==1)				num+=Math.pow(i, 2);		}		System.out.println(num);	}}