import java.util.Scanner;public class Main {    public static double getCircleArea(double r){        return Math.PI*r*r;    }    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = scanner.nextInt();        double r;        double[] S = new double[n];        for(int i=0;i<n;i++){             r = scanner.nextDouble();             S[i] = getCircleArea(r);        }        for (int i=0;i<n;i++){            System.out.println(S[i]);        }        System.out.println("END.");    }}