import java.util.Scanner;public class Main {	static public void main(String args[]) {		long  k;		double tar;		Scanner sc=new Scanner(System.in);		k=sc.nextLong();		tar=Math.sqrt(k);		long min_dis=Integer.MAX_VALUE;		for(int i=1;i<=tar;i++) {			if(k%i==0) {				long y=k/i;				long dis=y+i-2;				min_dis=Math.min(dis, min_dis);			}					}		System.out.println(min_dis);	}}