import java.text.SimpleDateFormat;import java.util.Scanner;class Points {	int x;	int y;	public Points(int x, int y) {		this.x = x;		this.y = y;	}}public class Main {	final static Scanner sc = new Scanner(System.in);	SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-DD");	public static void main(String[] args) {		long k = sc.nextLong();		long i = 0;		for (i = (long) Math.sqrt(k); i >= 2; i--) {			if (k % i == 0) {				break;			}		}		long x = i;		long y = k / i;		System.out.println(x - 1 + y - 1);	}}