import java.math.BigDecimal;import java.util.Scanner;public class Main {	static final Scanner sc = new Scanner(System.in);	public static void main(String[] args) {		String s = sc.nextLine();		char[] arr = s.toCharArray();		int len = s.length();		BigDecimal res = new BigDecimal("0");		for (int i = 2; i < len; i++) {			res = res.add(new BigDecimal(arr[i]).divide(new BigDecimal("8.0").pow(i - 1)));		}		res = res.stripTrailingZeros();		System.out.printf("%s[8]=%s[10]\n", s, res.toString());	}}