import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        long[] arr = new long[51];        arr[1] = 3;        arr[2] = 6;        arr[3] = 6;        for (int i = 4; i < 51; i++) {            arr[i] = arr[i - 1] + arr[i - 2] * 2;        }        while (sc.hasNext()) {            int n = sc.nextInt();            System.out.println(arr[n]);        }    }}