class Main{    public static void main(String[] args) {        for (int i = 1; i < 100000; i++) {            int sum = i;            if (sum%5==1){                sum -= sum/5 + 1;                if (sum%5 == 2){                    sum -= sum/5 + 2;                    if (sum%5 == 3){                        sum -= sum/5 + 3;                        if (sum%5 == 4) {                            sum -= sum/5 + 4;                            if (sum%5 == 0 &&sum!=0){                                System.out.println(i);                                return;                            }                        }                    }                }            }        }    }}