import java.util.Scanner;import static java.util.Arrays.sort;public class Main {    public static void main(String[] args)    {        Scanner in=new Scanner(System.in);        int n=in.nextInt();        int m=in.nextInt();        int[] a=new int[n];        int[] b=new int[m];        for(int i=0;i<n;i++)        {            a[i]=in.nextInt();        }        for(int i=0;i<m;i++)        {            b[i]=in.nextInt();        }        sort(a);        sort(b);        int min=Math.abs(a[0]-b[0]);        int i=0,j=0;        while(i!=n&&j!=m)        {            int num=Math.abs(a[i]-b[j]);            if(num<min)                min=num;            if(a[i]>a[j])            {                j++;            }            else                i++;            if(min==0)                break;        }        System.out.println(min);    }}