import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        String s ;        String t ;        s = input.next();        t = input.next();        int max = 100000;        int number = 0;        for(int i = 0 ; i < s.length() - t.length() + 1; i++)        {            for(int j = 0 ; j < t.length() ; j++)            {                if(s.charAt(i+j) != t.charAt(j))                {                    number++;                }            }            if(max > number)            {                max = number;            }            number = 0;        }        System.out.println(max);    }}