import java.util.*;public class Main {    static int N = 200010;    static long[] a = new long[N];    static long[] b = new long[N];    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        for (int i = 1; i <= n; i++) {            a[i] = sc.nextLong();            b[i] = a[i] - a[i - 1];        }        long ans = 0;        int l = 1, r = n;        while (l < r) {            while (b[l] > 0)                l++;            while (b[r] < 0)                r--;            if (l > r)                break;            long t = Math.min(Math.abs(b[l]), Math.abs(b[r])) + 1;            b[l] += t;            b[r] -= t;            ans += t;        }        System.out.println(ans);    }}